function share(button) {
    if (navigator.share) {
      var url = document.location.href;
      var title = document.getElementsByTagName("title")[0].innerHTML;
      var description = document
        .querySelector("meta[name=description]")
        .getAttribute("content");
  
      navigator.share({
        title: title,
        text: description,
        url: url
      });
    } else {
      if (!clickedOnShare) {
        clickedOnShare = true;
        showLoading(button);
      }
    }
  }